con:configuration.scriptText:
def header = messageExchange.getResponseHeaders()
def status =  header["#status#"]
log.info status[0]
if( status[0] == "HTTP/1.1 200 OK")
{
	//Set 1 for Pass testcase
	context.testCase.testSuite.project.setPropertyValue('Status', "1")
}
else
{
	//Set 2 for Fail testcase
	context.testCase.testSuite.project.setPropertyValue('Status', "2")
}

//Pass JIRA testcase ID to the API
context.testCase.testSuite.project.setPropertyValue('issues', "NS-2")

